mapper.select:
select t1.id, t3.name, t3.idNumber, t3.gender, t3.age, t3.phoneNumber, t3.education, t3.addedTime, t3.professionalSkills, t3.hasCertificate, t3.isDisability, t2.recommendEnterprise, t2.recommendJob, t4.signUpTime, t4.interviewTime, t1.note from interview_info as t1 inner join phone_call_list as t2 on t1.phoneCallListId = t2.id inner join (select * from customer_info
and name like concat('%', #{customerInfo.name}, '%')
and idNumber like concat('%', #{customerInfo.idNumber}, '%')
and phoneNumber like concat('%', #{customerInfo.phoneNumber}, '%')
) as t3 on t1.customerId = t3.id inner join sign_up_info t4 on t1.signUpId = t4.id inner join (select id from employee where phone = #{employeePhone}) t5 on t1.employeeId = t5.id limit #{startIndex}, 10;

select count(t1.id) from interview_info as t1 inner join phone_call_list as t2 on t1.phoneCallListId = t2.id inner join (select * from customer_info
and name like concat('%', #{customerInfo.name}, '%')
and idNumber like concat('%', #{customerInfo.idNumber}, '%')
and phoneNumber like concat('%', #{customerInfo.phoneNumber}, '%')
) as t3 on t1.customerId = t3.id inner join sign_up_info t4 on t1.signUpId = t4.id inner join (select id from employee where phone = #{employeePhone}) t5 on t1.employeeId = t5.id;

select t3.id as interviewInfoId, t2.name as customerName, t2.idNumber, t2.gender, t2.age, t2.phoneNumber, t2.education, t2.address, t2.professionalSkills, t2.hasCertificate, t2.isDisability, t1.recommendEnterprise, t1.recommendJob, t5.interviewTime, t4.deptName, t4.name as employeeName, t2.id as customerId, t5.id as signUpId, t1.id as phoneCallListId, t4.id as employeeId from (select * from phone_call_list
and recommendEnterprise like concat('%', #{recommendEnterprise}, '%')
) as t1 inner join (select * from customer_info
and name like concat('%', #{customerInfo.name}, '%')
and idNumber like concat('%', #{customerInfo.idNumber}, '%')
and gender like concat('%', #{customerInfo.gender}, '%')
and phoneNumber like concat('%', #{customerInfo.phoneNumber}, '%')
and address like concat('%', #{customerInfo.address}, '%')
) as t2 on t1.customerId = t2.id inner join (select * from interview_info where state = #{state}) as t3 on t1.customerId = t3.customerId and t1.employeeId = t3.employeeId and t1.id = t3.phoneCallListId inner join (select * from employee
and deptName like concat('%', #{deptName}, '%')
and name like concat('%', #{employeeName}, '%')
) as t4 on t1.employeeId = t4.id inner join (select * from sign_up_info
and interviewTime like concat('%', #{interviewTime}, '%')
) as t5 on t5.id = t3.signUpId limit #{startIndex}, 10;

select count(t3.id) from (select * from phone_call_list
and recommendEnterprise like concat('%', #{recommendEnterprise}, '%')
) as t1 inner join (select * from customer_info
and name like concat('%', #{customerInfo.name}, '%')
and idNumber like concat('%', #{customerInfo.idNumber}, '%')
and gender like concat('%', #{customerInfo.gender}, '%')
and phoneNumber like concat('%', #{customerInfo.phoneNumber}, '%')
and address like concat('%', #{customerInfo.address}, '%')
) as t2 on t1.customerId = t2.id inner join (select * from interview_info where state = #{state}) as t3 on t1.customerId = t3.customerId and t1.employeeId = t3.employeeId and t1.id = t3.phoneCallListId inner join (select * from employee
and deptName like concat('%', #{deptName}, '%')
and name like concat('%', #{employeeName}, '%')
) as t4 on t1.employeeId = t4.id inner join (select * from sign_up_info
and interviewTime like concat('%', #{interviewTime}, '%')
) as t5 on t5.id = t3.signUpId;

mapper.update:
update hrms.interview_info
note=#{note}, state=#{state}, customerId=#{customerId}, phoneCallListId=#{phoneCallListId}, signUpId=#{signUpId}, employeeId=#{employeeId},
where id = #{id};

mapper.select:
select * from interview_info


		and id = #{id}


		and note = #{note}


		and state = #{state}


		and customerId = #{customerId}


		and phoneCallListId = #{phoneCallListId}


		and signUpId = #{signUpId}


		and employeeId = #{employeeId}

select t1.id, t2.name as customerName, t2.idNumber, t2.gender, t2.age, t2.phoneNumber, t2.education, t2.address, t2.professionalSkills, t2.hasCertificate, t2.isDisability, t3.recommendEnterprise, t3.recommendJob, t5.signUpTime, t5.interviewTime, t1.note, t4.name as employeeName from interview_info as t1 inner join (select * from customer_info
and name like concat('%', #{customerInfo.name}, '%')
and idNumber like concat('%', #{customerInfo.idNumber}, '%')
and phoneNumber like concat('%', #{customerInfo.phoneNumber}, '%')
) as t2 on t1.customerId = t2.id inner join phone_call_list as t3 on t1.phoneCallListId = t3.id inner join (select * from employee
deptName = #{deptName}
and name like concat('%', #{employeeName}, '%')
) as t4 on t1.employeeId = t4.id inner join sign_up_info as t5 on t1.signUpId = t5.id
limit #{startIndex}, 10;
